import React, { Component } from 'react';
import axios from 'axios';

export default class Siswa extends Component {
  constructor() {
    super();
    this.state = {
      siswa: [],
      nis: '',
      nama: '',
      alamat: '',
      action: '',
    };
  }

  componentDidMount() {
    // Memuat data siswa saat komponen dimuat
    this.loadSiswaData();
  }

  loadSiswaData = () => {
    axios.get('http://localhost:1928/siswa')
      .then(response => {
        this.setState({ siswa: response.data });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  bind = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  SaveSiswa = (event) => {
    event.preventDefault();
    const { nis, nama, alamat, action } = this.state;

    axios.post('http://localhost:1928/siswa/save', { nis, nama, alamat, action })
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData(); // Memuat ulang data siswa setelah menyimpan
      })
      .catch(error => {
        console.error('Error saving data:', error);
      });
  };

  Drop = (nis) => {
    axios.delete(`http://localhost:5000/api/siswa/delete/${nis}`)
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData(); // Memuat ulang data siswa setelah menghapus
      })
      .catch(error => {
        console.error('Error deleting data:', error);
      });
  };

  render() {
    return (
      <div className="container">
        <h1>Data Siswa</h1>
        <form onSubmit={this.SaveSiswa}>
          <input type="text" name="nis" placeholder="NIS" value={this.state.nis} onChange={this.bind} required />
          <input type="text" name="nama" placeholder="Nama" value={this.state.nama} onChange={this.bind} required />
          <input type="text" name="alamat" placeholder="Alamat" value={this.state.alamat} onChange={this.bind} required />
          <select name="action" onChange={this.bind} value={this.state.action}>
            <option value="insert">Insert</option>
            <option value="update">Update</option>
          </select>
          <button type="submit">Simpan</button>
        </form>
        <table>
          <thead>
            <tr>
              <th>NIS</th>
              <th>Nama</th>
              <th>Alamat</th>
              <th>Aksi</th>
            </tr>
          </thead>
          <tbody>
            {this.state.siswa.map((item, index) => (
              <tr key={index}>
                <td>{item.nis}</td>
                <td>{item.nama}</td>
                <td>{item.alamat}</td>
                <td>
                  <button onClick={() => this.Drop(item.nis)}>Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
