import React, { Component } from 'react';
import axios from 'axios';

class Siswa extends Component {
  constructor() {
    super();
    this.state = {
      siswa: [],
      nis: '',
      nama: '',
      alamat: '',
      action: ''
    };
  }

  componentDidMount() {
    this.loadSiswaData();
  }

  loadSiswaData = () => {
    axios.get('http://localhost:3000/siswa')
      .then(response => {
        this.setState({ siswa: response.data });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  bind = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  saveSiswa = (event) => {
    event.preventDefault();
    const { nis, nama, alamat, action } = this.state;

    axios.post('http://localhost:3000/siswa/save', { nis, nama, alamat })
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData();
      })
      .catch(error => {
        console.error('Error saving data:', error);
      });
  };

  updateSiswa = () => {
    const { nis, nama, alamat, action } = this.state;

    axios.put(`http://localhost:3000/siswa/update/${nis}`, { nama, alamat })
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData();
      })
      .catch(error => {
        console.error('Error updating data:', error);
      });
  };

  deleteSiswa = (nis) => {
    axios.delete(`http://localhost:3000/siswa/${nis}`)
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData();
      })
      .catch(error => {
        console.error('Error deleting data:', error);
      });
  };

  render() {
    return (
      <div>
        <h1>Data Siswa</h1>
        <form onSubmit={this.saveSiswa}>
          <input type="text" name="nis" value={this.state.nis} onChange={this.bind} placeholder="NIS" required /><br />
          <input type="text" name="nama" value={this.state.nama} onChange={this.bind} placeholder="Nama" required /><br />
          <input type="text" name="alamat" value={this.state.alamat} onChange={this.bind} placeholder="Alamat" required /><br />
          <button type="submit">Simpan</button>
        </form>
        <table>
          <thead>
            <tr>
              <th>NIS</th>
              <th>Nama</th>
              <th>Alamat</th>
              <th>Aksi</th>
            </tr>
          </thead>
          <tbody>
            {this.state.siswa.map((item, index) => (
              <tr key={index}>
                <td>{item.nis}</td>
                <td>{item.nama}</td>
                <td>{item.alamat}</td>
                <td>
                  <button onClick={() => this.updateSiswa(item.nis)}>Edit</button>
                  <button onClick={() => this.deleteSiswa(item.nis)}>Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Siswa;
