import React from 'react';
import $ from 'jquery';
import axios from 'axios';

class Buku extends React.Component {
    constructor() {
        super();
        this.state = {
            buku: [],
            isbn: "",
            judul: "",
            pengarang: "",
            action: "",
            search: "",
        };
    }

    bind = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }

    Close = () => {
        $("#modal").hide();
    }

    Add = () => {
        $("#modal").show();
        this.setState({
            isbn: "",
            judul: "",
            pengarang: "",
            action: "insert"
        });
    }

    Edit = (item) => {
        $("#modal").show();
        this.setState({
            isbn: item.isbn,
            judul: item.judul,
            pengarang: item.pengarang,
            action: "update",
            selectedItem: item
        });
    }

    getBuku = () => {
        let url = "http://localhost:1928/buku";
        axios.get(url)
            .then(response => {
                this.setState({ buku: response.data.buku });
            })
            .catch(error => {
                console.log(error);
            });
    }

    findBuku = (event) => {
        let url = "http://localhost:1928/buku";
        if (event.keyCode === 13) {
            let form = {
                find: this.state.search
            }
            axios.post(url, form)
                .then(response => {
                    this.setState({ buku: response.data.buku });
                })
                .catch(error => {
                    console.log(error);
                });
        }
    }

    SaveBuku = (event) => {
        event.preventDefault();
        let url = this.state.action === "insert" ? "http://localhost:1928/buku/save" : "http://localhost:1928/buku/update";
        let form = {
            isbn: this.state.isbn,
            judul: this.state.judul,
            pengarang: this.state.pengarang
        }
        axios.post(url, form)
            .then(response => {
                this.getBuku();
            })
            .catch(error => {
                console.log(error);
            });
        $("#modal").hide();
    }

    Drop = (isbn) => {
        let url = "http://localhost:1928/buku/" + isbn;
        if (window.confirm('Apakah Anda yakin ingin menghapus data ini?')) {
            axios.delete(url)
                .then(response => {
                    this.getBuku();
                })
                .catch(error => {
                    console.log(error);
                });
        }
    }

    componentDidMount() {
        this.getBuku();
    }

    render() {
        return (
            <div className="container">
                <br />
                <h4 className="d-flex justify-content-between align-items-center mb-3">
                    <span className="display-6">Data Buku</span>
                </h4>
                <br />
                <div className="col-md-5">
                    <div className="form">
                        <input
                            type="text"
                            name="search"
                            value={this.state.search}
                            onChange={this.bind}
                            onKeyUp={this.findBuku}
                            className="form-control form-input"
                            placeholder="Cari Buku"
                        />
                    </div>
                </div>
                <br />
                <div className="container">
                </div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ISBN</th>
                            <th>Judul</th>
                            <th>Pengarang</th>
                            <th>Option</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.buku.map((item, index) => {
                            return (
                                <tr key={index}>
                                    <td>{item.isbn}</td>
                                    <td>{item.judul}</td>
                                    <td>{item.pengarang}</td>
                                    <td>
                                        <button
                                            className="btn btn-sm btn-outline-success m-1"
                                            data-toggle="modal"
                                            data-target="#modal"
                                            onClick={() => this.Edit(item)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="btn btn-sm btn-danger m-1"
                                            onClick={() => this.Drop(item.isbn)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
                <br />
                <button
                    className="btn btn-primary btn-lg"
                    onClick={this.Add}
                    data-toggle="modal"
                    data-target="#modal"
                >
                    Tambah Buku
                </button>

                <div className="modal modal" id="modal">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h4 className="modal-title"><b>Data Buku</b></h4>
                                <button
                                    type="button"
                                    className="btn-close"
                                    data-bs-dismiss="modal"
                                    aria-label="Close"
                                    onClick={() => this.Close()}
                                ></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={this.SaveBuku}>
                                    ISBN
                                    <input
                                        type="text"
                                        name="isbn"
                                        value={this.state.isbn}
                                        onChange={this.bind}
                                        className="form-control"
                                        required
                                    />
                                    Judul Buku
                                    <input
                                        type="text"
                                        name="judul"
                                        value={this.state.judul}
                                        onChange={this.bind}
                                        className="form-control"
                                        required
                                    />
                                    Pengarang Buku
                                    <input
                                        type="text"
                                        name="pengarang"
                                        value={this.state.pengarang}
                                        onChange={this.bind}
                                        className="form-control"
                                        required
                                    />
                                    <button className="btn btn-primary btn-block" type="submit">
                                        Save
                                    </button>
                                </form>
                                <br />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Buku;
