import React, { Component } from 'react'
import $ from 'jquery';
import '../index.css';
import axios from 'axios';

export default class Customer extends Component {
  constructor() {
    super();
    this.state = {
      customer: [],
      id_cust: '',
      nama: '',
      alamat: '',
      action: '',
    };
  }

  componentDidMount() {
    // Memuat data siswa saat komponen dimuat
    this.loadCustomerData();
  }

  loadCustomerData = () => {
    axios.get('http://localhost:1928/customer')
      .then(response => {
        this.setState({ customer: response.data });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  bind = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  saveCustomer = (event) => {
    event.preventDefault();
    const { id_cust, nama, alamat } = this.state;
  
    axios.post('http://localhost:1928/customer/save', { id_cust, nama, alamat })
      .then(response => {
        console.log(response.data.message);
        this.setState({ id_cust: '', nama: '', alamat: '' }); // Bersihkan input setelah penyimpanan
        this.loadCustomerData(); // Memuat ulang data siswa setelah menyimpan
      })
      .catch(error => {
        console.error('Error saving data:', error);
      });
  };
  

  updateCustomer = () => {
    const { id_cust, nama, alamat } = this.state;
  
    axios.put(`http://localhost:1928/customer/update/${id_cust}`, { id_cust, nama, alamat })
      .then(response => {
        console.log(response.data.message);
        this.loadCustomerData(); // Memuat ulang data siswa setelah melakukan pembaruan
      })
      .catch(error => {
        console.error('Error updating data:', error);
      });
  };
  
  

  deleteCustomer = (id_cust) => {
  axios.delete(`http://localhost:1928/customer/${id_cust}`)
    .then(response => {
      console.log(response.data.message);
      this.loadCustomerData(); // Memuat ulang data siswa setelah menghapus
    })
    .catch(error => {
      console.error('Error deleting data:', error);
    });
};


    render() {
        return (
            <div className="container"> <br></br>
            <h4 className="d-flex justify-content-between align-items-center mb-3">
                <span className="display-6">Customer Data</span>
                </h4><br></br>
                <div class="col-md-5">
                        <div class="form"><input type="text" value={this.state.keyword} onChange={ev => this.setState({keyword: ev.target.value})} onKeyUp={ev => this.searching(ev)} class="form-control form-input" placeholder="Find customer"/></div>
                        </div><br></br>
                <div class="container">
                

                        <br></br>
                        <button className="btn btn-dark btn-lg" onClick={() => this.Add()} data-toggle="modal" data-target="#modal">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-plus-circle" viewBox="0 0 16 16">
                <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"/>
                <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4z"/>
                </svg> Add New Customer
                </button>

                


                {/* Modal untuk manipulasi data */}
                <div className="modal modal" id="modal">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h4 className="modal-title"><b>Customer Data</b></h4>
                                <button
                                    type="button"
                                    className="btn-close"
                                    data-bs-dismiss="modal"
                                    aria-label="Close"
                                    onClick={() => this.Close()}
                                ></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={this.saveCustomer}>
                                    Id_Customer
                                    <input
                                        type="number"
                                        name="ID Customer"
                                        value={this.state.id_cust}
                                        onChange={this.bind}
                                        className="form-control"
                                        required
                                    />
                                    Nama
                                    <input
                                        type="text"
                                        name="nama"
                                        value={this.state.nama}
                                        onChange={this.bind}
                                        className="form-control"
                                        required
                                    />
                                    Alamat
                                    <input
                                        type="text"
                                        name="alamat"
                                        value={this.state.alamat}
                                        onChange={this.bind}
                                        className="form-control"
                                        required
                                    />
                                    <button className="btn btn-dark btn-block" type="submit">
                                        Save
                                    </button>
                                </form>
                                <br />
                            </div>
                        </div>
                    </div>
                </div>

                    </div>
                    
            </div>

        )
    }
}
