import React, { Component } from 'react'
import $ from 'jquery';
import '../index.css';
import axios from 'axios';

export default class Siswa extends Component {
  constructor() {
    super();
    this.state = {
      siswa: [],
      nis: '',
      nama: '',
      alamat: '',
      action: '',
    };
  }

  componentDidMount() {
    // Memuat data siswa saat komponen dimuat
    this.loadSiswaData();
  }

  loadSiswaData = () => {
    axios.get('http://localhost:1928/siswa')
      .then(response => {
        this.setState({ siswa: response.data });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  bind = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  saveSiswa = (event) => {
    event.preventDefault();
    const { nis, nama, alamat, action } = this.state;

    axios.post('http://localhost:1928/siswa/save', { nis, nama, alamat, action: 'insert' })
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData(); // Memuat ulang data siswa setelah menyimpan
      })
      .catch(error => {
        console.error('Error saving data:', error);
      });
  };

  updateSiswa = () => {
    const { nis, nama, alamat, action } = this.state;
  
    axios.put(`http://localhost:1928/siswa/update/${nis}`, { nama, alamat, action: 'update' })
    .then(response => {
        console.log(response.data.message);
        this.loadSiswaData(); // Memuat ulang data siswa setelah melakukan pembaruan
      })
      .catch(error => {
        console.error('Error updating data:', error);
      });
  };
  

  deleteSiswa = (nis) => {
    axios.delete(`http://localhost:5000/siswa/${nis}`)
    .then(response => {
        console.log(response.data.message);
        this.loadSiswaData(); // Memuat ulang data siswa setelah menghapus
      })
      .catch(error => {
        console.error('Error deleting data:', error);
      });
  };

  render() {
    return (
        <div className="container">
            <br />
            <h4 className="d-flex justify-content-between align-items-center mb-3">
                <span className="display-6">Customer Data</span>
            </h4>
            <br />
            <div className="col-md-5">
                <div className="form">
                    <input
                        type="text"
                        name="search"
                        value={this.state.search}
                        onChange={this.bind}
                        onKeyUp={this.findSiswq}
                        className="form-control form-input"
                        placeholder="Find Officer"
                    />
                </div>
            </div>
            <br />
            <div className="container">
            </div>
            <table className="table">
                <thead>
                    <tr>
                        <th>NIS</th>
                        <th>Nama</th>
                        <th>Alamat</th>
                        <th>Option</th>
                    </tr>
                </thead>
                <tbody>
                    {this.state.siswa.map((item, index) => {
                        return (
                            <tr key={index}>
                                <td>{item.nis}</td>
                                <td>{item.nama}</td>
                                <td>{item.alamat}</td>
                                <td>
                                    <button
                                        className="btn btn-sm btn-outline-dark m-1"
                                        data-toggle="modal"
                                        data-target="#modal"
                                        onClick={() => this.Edit(item)}
                                    >
                                        Edit
                                    </button>
                                    <button
                                        className="btn btn-sm btn-dark m-1"
                                        onClick={() => this.Drop(item.nis)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>
            <br />
            <button
                className="btn btn-dark btn-lg"
                onClick={this.Add}
                data-toggle="modal"
                data-target="#modal"
            >
                Add New Customer
            </button>

            {/* Modal untuk manipulasi data */}
            <div className="modal modal" id="modal">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h4 className="modal-title"><b>Customer Data</b></h4>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                                onClick={() => this.Close()}
                            ></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={this.SavePegawai}>
                                NIP
                                <input
                                    type="number"
                                    name="nip"
                                    value={this.state.nip}
                                    onChange={this.bind}
                                    className="form-control"
                                    required
                                />
                                Nama
                                <input
                                    type="text"
                                    name="nama"
                                    value={this.state.nama}
                                    onChange={this.bind}
                                    className="form-control"
                                    required
                                />
                                Alamat
                                <input
                                    type="text"
                                    name="alamat"
                                    value={this.state.alamat}
                                    onChange={this.bind}
                                    className="form-control"
                                    required
                                />
                                <button className="btn btn-dark btn-block" type="submit">
                                    Save
                                </button>
                            </form>
                            <br />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
}
