import React, { Component } from 'react';
import axios from 'axios';

export default class Siswa extends Component {
  constructor() {
    super();
    this.state = {
      siswa: [],
      nis: '',
      nama: '',
      alamat: '',
      keyword: '', // Tambah state untuk keyword pencarian
      action: '',
    };
  }

  componentDidMount() {
    // Memuat data siswa saat komponen dimuat
    this.loadSiswaData();
  }

  loadSiswaData = () => {
    axios.get('http://localhost:1928/siswa')
      .then(response => {
        this.setState({ siswa: response.data });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  bind = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  saveSiswa = (event) => {
    event.preventDefault();
    const { nis, nama, alamat, action } = this.state;

    axios.post('http://localhost:1928/siswa/save', { nis, nama, alamat, action: 'insert' })
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData(); // Memuat ulang data siswa setelah menyimpan
      })
      .catch(error => {
        console.error('Error saving data:', error);
      });
  };

  updateSiswa = () => {
    const { nis, nama, alamat, action } = this.state;
  
    axios.put(`http://localhost:1928/siswa/update/${nis}`, { nama, alamat, action: 'update' })
    .then(response => {
        console.log(response.data.message);
        this.loadSiswaData(); // Memuat ulang data siswa setelah melakukan pembaruan
      })
      .catch(error => {
        console.error('Error updating data:', error);
      });
  };
  

  deleteSiswa = (nis) => {
    axios.delete(`http://localhost:5000/siswa/${nis}`)
    .then(response => {
        console.log(response.data.message);
        this.loadSiswaData(); // Memuat ulang data siswa setelah menghapus
      })
      .catch(error => {
        console.error('Error deleting data:', error);
      });
  };

  searchSiswa = () => {
    axios.get(`http://localhost:1928/siswa/search/${this.state.keyword}`)
      .then(response => {
        this.setState({ siswa: response.data });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  render() {
    return (
      <div className="container">
        <br />
        <h4 className="d-flex justify-content-between align-items-center mb-3">
          <span className="display-6">Customer Data</span>
        </h4>
        <br />
        <div className="col-md-5">
          <div className="form">
            <input
              type="text"
              value={this.state.keyword}
              onChange={(ev) => this.setState({ keyword: ev.target.value })}
              onKeyUp={this.searchSiswa}
              className="form-control form-input"
              placeholder="Find customer"
            />
          </div>
        </div>
        <br />
        <div className="container">
          {/* Tabel data siswa */}
          <table className="table">
            <thead>
              <tr>
                <th>Customer ID</th>
                <th>Name</th>
                <th>Address</th>
                <th>Option</th>
              </tr>
            </thead>
            <tbody>
              {this.state.siswa.map((item, index) => {
                return (
                  <tr key={index}>
                    <td>{item.nis}</td>
                    <td>{item.nama}</td>
                    <td>{item.alamat}</td>
                    <td>
                      <button
                        className="btn btn-outline-dark m2"
                        onClick={() => this.Edit(item)}
                        data-toggle="modal"
                        data-target="#modal"
                      >
                        {/* Icon Edit */}
                      </button>
                      <button
                        className="btn btn-dark m1"
                        onClick={() => this.deleteSiswa(item.nis)}
                      >
                        {/* Icon Delete */}
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          <br />
          <button
            className="btn btn-dark btn-lg"
            onClick={() => this.Add()}
            data-toggle="modal"
            data-target="#modal"
          >
            Add New Customer
          </button>

          {/* Component modal sbg control manipulasi data */}
          <div className="modal modal" id="modal">
            {/* Isi modal */}
          </div>
        </div>
      </div>
    );
  }
}
