import React, { Component } from 'react';
import axios from 'axios';

class Siswa extends Component {
  constructor() {
    super();
    this.state = {
      siswa: [],
      nis: '',
      nama: '',
      alamat: '',
      action: '',
      search: '', // tambahkan state untuk fitur pencarian
    };
  }

  componentDidMount() {
    this.loadSiswaData();
  }

  loadSiswaData = () => {
    axios.get('http://localhost:1928/')
      .then(response => {
        this.setState({ siswa: response.data });
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  bind = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  saveSiswa = (event) => {
    event.preventDefault();
    const { nis, nama, alamat, action } = this.state;

    axios.post('http://localhost:1928/siswa/save', { nis, nama, alamat, action })
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData();
      })
      .catch(error => {
        console.error('Error saving data:', error);
      });
  };

  updateSiswa = () => {
    const { nis, nama, alamat, action } = this.state;
  
    axios.put(`http://localhost:1928/siswa/update/${nis}`, { nama, alamat, action })
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData();
      })
      .catch(error => {
        console.error('Error updating data:', error);
      });
  };
  
  deleteSiswa = (nis) => {
    axios.delete(`http://localhost:1928/siswa/${nis}`)
      .then(response => {
        console.log(response.data.message);
        this.loadSiswaData();
      })
      .catch(error => {
        console.error('Error deleting data:', error);
      });
  };

  searching = (event) => {
    if (event.keyCode === 13) {
      let url = "http://localhost:1928/siswa";
      let form = {
        find: this.state.search
      }
      axios.post(url, form)
        .then(response => {
          this.setState({ siswa: response.data });
        })
        .catch(error => {
          console.log(error);
        });
    }
  };

  render() {
    return (
      <div className="container">
        <br />
        <h4 className="d-flex justify-content-between align-items-center mb-3">
          <span className="display-6">Customer Data</span>
        </h4>
        <br />
        <div className="col-md-5">
          <div className="form">
            <input
              type="text"
              value={this.state.search}
              onChange={(ev) => this.setState({ search: ev.target.value })}
              onKeyUp={(ev) => this.searching(ev)}
              className="form-control form-input"
              placeholder="Find customer"
            />
          </div>
        </div>
        <br />
        <div className="container">
          {/* Place your table for displaying siswa data here */}
        </div>
        {/* Rest of your JSX content for displaying siswa data and modal goes here */}
      </div>
    );
  }
}

export default Siswa;
