import React from 'react';
import $ from 'jquery';
import axios from 'axios';

class GudangBuku extends React.Component {
    constructor() {
        super();
        this.state = {
            buku: [], // array untuk menampung data buku
            kode: "",
            judul: "",
            penulis: "",
            penerbit: "",
            qty: "",
        };
    }

    // Fungsi untuk mengubah state berdasarkan perubahan input
    bind = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }

    // Menutup modal
    close = () => {
        $("#modal").hide();
    }

    // Menampilkan form kosong untuk menambahkan buku baru
    add = () => {
        $("#modal").show();
        this.setState({
            kode: "",
            judul: "",
            penulis: "",
            penerbit: "",
            qty:"",
            action: "insert"
        });
    }

    // Mengisi form dengan data buku yang dipilih untuk diedit
    edit = (item) => {
        $("#modal").show();
        this.setState({
            kode: item.kode,
            judul: item.judul,
            penulis: item.penulis,
            penerbit: item.penulis,
            penulis: item.penulis,

            action: "update",
            selectedItem: item
        });
    }

    // Mengambil data buku dari backend
    getBooks = () => {
        let url = "http://localhost:1928/books";
        axios.get(url)
            .then(response => {
                this.setState({ books: response.data.books });
            })
            .catch(error => {
                console.log(error);
            });
    }

    // Fungsi pencarian berdasarkan keyword
    findBooks = (event) => {
        let url = "http://localhost:1928/books";
        if (event.keyCode === 13) {
            let form = {
                find: this.state.search
            }
            axios.post(url, form)
                .then(response => {
                    this.setState({ books: response.data.books });
                })
                .catch(error => {
                    console.log(error);
                });
        }
    }

    // Menyimpan atau memperbarui data buku ke backend
    saveBook = (event) => {
        event.preventDefault();
        let url = this.state.action === "insert" ? "http://localhost:1928/books/save" : "http://localhost:1928/books/update";
        let form = {
            isbn: this.state.isbn,
            title: this.state.title,
            author: this.state.author
        }
        axios.post(url, form)
            .then(response => {
                this.getBooks(); // Memanggil data yang terbaru setelah simpan berhasil
            })
            .catch(error => {
                console.log(error);
            });
        $("#modal").hide(); // Menutup form modal
    }

    // Menghapus data buku berdasarkan ISBN
    dropBook = (isbn) => {
        let url = "http://localhost:1928/books/" + isbn;
        if (window.confirm('Apakah Anda yakin ingin menghapus data ini?')) {
            axios.delete(url)
                .then(response => {
                    this.getBooks(); // Memanggil data yang terbaru setelah hapus berhasil
                })
                .catch(error => {
                    console.log(error);
                });
        }
    }

    componentDidMount() {
        this.getBooks(); // Memanggil data buku pertama kali saat komponen dimuat
    }

    render() {
        return (
            <div className="container">
                <br />
                <h4 className="d-flex justify-content-between align-items-center mb-3">
                    <span className="display-6">Data Buku</span>
                </h4>
                <br />
                <div className="col-md-5">
                    <div className="form">
                        <input
                            type="text"
                            name="search"
                            value={this.state.search}
                            onChange={this.bind}
                            onKeyUp={this.findBooks}
                            className="form-control form-input"
                            placeholder="Cari Buku"
                        />
                    </div>
                </div>
                <br />
                <div className="container">
                </div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ISBN</th>
                            <th>Judul</th>
                            <th>Penulis</th>
                            <th>Option</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.books.map((item, index) => {
                            return (
                                <tr key={index}>
                                    <td>{item.isbn}</td>
                                    <td>{item.title}</td>
                                    <td>{item.author}</td>
                                    <td>
                                        <button
                                            className="btn btn-sm btn-outline-success m-1"
                                            data-toggle="modal"
                                            data-target="#modal"
                                            onClick={() => this.edit(item)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="btn btn-sm btn-danger m-1"
                                            onClick={() => this.dropBook(item.isbn)}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
                <br />
                <button
                    className="btn btn-primary btn-lg"
                    onClick={this.add}
                    data-toggle="modal"
                    data-target="#modal"
                >
                    Tambah Buku
                </button>

                {/* Modal untuk manipulasi data */}
                <div className="modal modal" id="modal">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h4 className="modal-title"><b>Data Buku</b></h4>
                                <button
                                    type="button"
                                    className="btn-close"
                                    data-bs-dismiss="modal"
                                    aria-label="Close"
                                    onClick={() => this.close()}
                                ></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={this.saveBook}>
                                    ISBN
                                    <input
                                        type="text"
                                        name="isbn"
                                        value={this.state.isbn}
                                        onChange={this.bind}
                                        className="form-control"
                                        required
                                    />
                                    Judul Buku
                                    <input
                                        type="text"
                                        name="title"
                                        value={this.state.title}
                                        onChange={this.bind}
                                        className="form-control"
                                        required
                                    />
                                    Penulis Buku
                                    <input
                                        type="text"
                                        name="author"
                                        value={this.state.author}
                                        onChange={this.bind}
                                        className="form-control"
                                        required
                                    />
                                    <button className="btn btn-primary btn-block" type="submit">
                                        Save
                                    </button>
                                </form>
                                <br />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default GudangBuku;
